//九九乘法表
public class chengfabiao {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       for(int i=1;i<=9;i++){
    	   //i代表行
    	   for(int j=1;j<=i;j++){
    		   System.out.print(i+"*"+j+"="+(i*j)+"\t");
    	   }
    	   System.out.println();
    	   
    	   
       }
	}

}
